package com.list.programs;

public class OperationalMethodCall {

		
	static void operations() {
		int a = 20, b = 10;
		
		int sum = a + b;
		int sub = a - b;
		int mul = a * b;
		int div = a / b;
		int mod = a % b;
		
		System.out.println("Calculated values are \n"+sum +"\n" +sub +"\n" +mul +"\n" +div +"\n" +mod);
		
		
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		operations();
				

	}

}
